import React, { useContext, useEffect, useRef } from "react";  
import './Home.css';  
import { ThemeContext } from "../context/ThemeContext";

function Home() {
    const { theme, changeTheme } = useContext(ThemeContext);  
    const inputRef = useRef(null);

    useEffect(() => {
        const root = document.getElementById('root');
        if(theme === 'light') {
            root.classList.remove('dark-mode');
            root.classList.add('light-mode');
        } else {
            root.classList.remove('light-mode');
            root.classList.add('dark-mode');
        }
    }, [theme]);

    return (
        <div className={`home ${theme}`}>  
            <header className="home-header">
                <h1>Welcome to Our Home Page!</h1>
                <p>{theme}</p>
                <button onClick={changeTheme} className="home-btn">Ganti Theme</button>
            </header>
            
            <section className="home-features">
                <div className="feature-card">
                    <img src="https://via.placeholder.com/150" alt="Feature 1" />
                    <h3>Feature One</h3>
                    <p>Discover the amazing benefits of our first feature that enhances your experience.</p>
                </div>
                <div className="feature-card">
                    <img src="https://via.placeholder.com/150" alt="Feature 2" />
                    <h3>Feature Two</h3>
                    <p>Our second feature provides you with incredible functionality and ease of use.</p>
                </div>
                <div className="feature-card">
                    <img src="https://via.placeholder.com/150" alt="Feature 3" />
                    <h3>Feature Three</h3>
                    <p>Learn more about the third feature that is designed to help you achieve more.</p>
                </div>
            </section>
            
            <section className="home-cta">
                <h2>Ready to Dive In?</h2>
                <p>Join us today and start making a difference with our comprehensive solutions.</p>
                <button className="home-btn">Join Now</button>
            </section>
        </div>
    );
}

export default Home;
