import React, { useContext, useEffect, useRef } from "react";  
import './Home.css';  
import { ThemeContext } from "../context/ThemeContext";

function Home() {
    const { theme, changeTheme } = useContext(ThemeContext);  
    const inputRef = useRef(null);

    useEffect(() => {
        const root = document.getElementById('root');
        if(theme === 'light') {
            root.classList.remove('dark-mode');
            root.classList.add('light-mode');
        } else {
            root.classList.remove('light-mode');
            root.classList.add('dark-mode');
        }
    }, [theme]);

    return (
        <div className={`home ${theme}`}>  
            <header className="home-header">
                <h1>Welcome to Logee</h1>
                <p>Transforming Your Logistics Experience</p>
                <button onClick={changeTheme} className="home-btn">
                    Switch to {theme === 'light' ? 'Dark' : 'Light'} Mode
                </button>
            </header>

            <section className="home-intro">
                <h2>What is Logee?</h2>
                <p>
                    Logee is a cutting-edge digital logistics solution designed to connect businesses 
                    with all players in the logistics ecosystem. Our platform empowers companies to 
                    optimize their supply chain management and streamline operations for maximum efficiency.
                </p>
            </section>
            
            <section className="home-features">
                <h2>Why Choose Logee?</h2>
                <div className="feature-card">
                    <img src="https://via.placeholder.com/150" alt="Feature 1" />
                    <h3>Seamless Integration</h3>
                    <p>
                        Logee seamlessly integrates with your existing systems, enabling you to 
                        manage orders, track shipments, and handle logistics all in one place.
                    </p>
                </div>
                <div className="feature-card">
                    <img src="https://via.placeholder.com/150" alt="Feature 2" />
                    <h3>Real-Time Insights</h3>
                    <p>
                        Gain real-time visibility into your logistics processes with detailed reports 
                        and analytics, ensuring smarter, data-driven decisions.
                    </p>
                </div>
                <div className="feature-card">
                    <img src="https://via.placeholder.com/150" alt="Feature 3" />
                    <h3>Collaborative Ecosystem</h3>
                    <p>
                        Logee connects all logistics players—from manufacturers and distributors 
                        to third-party logistics providers—creating an ecosystem that works in harmony.
                    </p>
                </div>
            </section>

            <section className="home-cta">
                <h2>Get Started with Logee</h2>
                <p>
                    Ready to enhance your business operations? Join the Logee ecosystem today 
                    and revolutionize the way you manage logistics.
                </p>
                <button className="home-btn">Register Now</button>
            </section>
        </div>
    );
}

export default Home;
